INSERT INTO tx_ximacontentplanner_note (uid, pid, tstamp, crdate, deleted, hidden, title, content) VALUES (1, 0, UNIX_TIMESTAMP(), UNIX_TIMESTAMP(), 0, 0, 'Welcome to Content Planner', '<p>This is a quick start note for the TYPO3 extension ‘Content Planner’. Use the dashboard to get a quick overview of the status of the content with the feature-rich widgets and to quickly capture specially assigned content. Further information on use and configuration can be found at <a href="https://github.com/xima-media/xima-typo3-content-planner?tab=readme-ov-file#usage" target="_blank">https://github.com/xima-media/xima-typo3-content-planner</a>.</p>');
